public List<String> getAccountlist() {

	try {
		return web3j.ethAccounts().send().getAccounts();
	} catch (Exception e) {
		e.printStackTrace();
	}
	return null;
}


public String getAccount(int index) {
	String account = null;

	try {
		account = web3j.ethAccounts().send().getAccounts().get(index);

	} catch (IOException e) {
		// TODO Auto-generated catch block
		e.printStackTrace();
	}
	return account;
}


